import{_ as l,c as i,o as e,a6 as a}from"./chunks/framework.BOyJDVBP.js";const b=JSON.parse('{"title":"什么是前端工程化","description":"","frontmatter":{"title":"什么是前端工程化","date":"2022-07-14T20:00:00.000Z","categories":["前端工程化"]},"headers":[],"relativePath":"posts/growth-record/engineering/index.md","filePath":"posts/growth-record/engineering/index.md","lastUpdated":1713496418000}'),t={name:"posts/growth-record/engineering/index.md"},r=a('<h1 id="什么是前端工程化" tabindex="-1">什么是前端工程化 <a class="header-anchor" href="#什么是前端工程化" aria-label="Permalink to &quot;什么是前端工程化&quot;">​</a></h1><p><strong>什么是前端工程化？</strong></p><p>是指将前端开发的流程<strong>规范化、标准化</strong>、包括开发流程、技术选型、代码规范、构建发布等，用于提升前端工程师的开发效率和代码质量。</p><p><strong>为什么要前端工程化？</strong></p><ul><li>复杂度搞：前端项目的多功能、都页面、多状态、多系统；</li><li>规模大：团队开发，多人协作，代码质量管理；</li><li>要求高：页面性能优化（CDN / 异步加载 / 请求合并），CSS 兼容性、单页面应用、服务端渲染...</li></ul><p><strong>怎么做到前端工程化？</strong></p><ol><li><p>从业务着手：简单的单页面应用，使用 Gulp + 同步工具实现开发全流程。</p></li><li><p>从复杂度考虑：框架最终要服务于我们的项目，而非累死累活的去维护框架。</p></li></ol><ul><li>Jenkins；</li><li>Git/Gitlab;</li><li>Webpack;</li><li>React/Vue/Angular;</li></ul><ol start="3"><li>从已知向未知扩展：不同的技术有不同的适应点，选择适合的才是最好的。</li></ol><p><strong>考虑</strong>：</p><ul><li>前后端技术是否更好的融合；</li><li>前后端分离，接口安全性；</li><li>Vue 或 React 是否适合制作静态页面；</li></ul><p>业务、技术上说服<strong>多用数字证据说话。</strong></p><p>团队的吸收能力、学习能力、知识拓展能力。</p><p>往好的方向去考虑事情、往好的方向营造这个氛围、往好的方向扩展我们的知识。</p><h2 id="一、开发流程" tabindex="-1">一、开发流程 <a class="header-anchor" href="#一、开发流程" aria-label="Permalink to &quot;一、开发流程&quot;">​</a></h2><ul><li>需求分析；</li><li>版本控制；</li><li>缺陷管理；</li><li>文档管理；</li><li>自动化；</li><li>性能测试；</li><li>发布部署；</li></ul><h2 id="二、技术选型" tabindex="-1">二、技术选型 <a class="header-anchor" href="#二、技术选型" aria-label="Permalink to &quot;二、技术选型&quot;">​</a></h2><ol><li>UI 框架：</li></ol><ul><li>Element UI;</li><li>Ant;</li><li>IView;</li><li>Mint;</li><li>Cube;</li></ul><ol start="2"><li>JS 框架：</li></ol><ul><li>MVVM;</li></ul><ol start="3"><li>Template:</li></ol><h2 id="三、代码规范" tabindex="-1">三、代码规范 <a class="header-anchor" href="#三、代码规范" aria-label="Permalink to &quot;三、代码规范&quot;">​</a></h2><ol><li>标准：</li></ol><ul><li>Standard;</li><li>airbnb;</li><li>Prettier;</li></ul><ol start="2"><li>公司：</li></ol><ul><li>谷歌；</li><li>百度；</li><li>腾讯；</li><li>美团；</li><li>饿了么；</li></ul><ol start="3"><li>模块化：</li></ol><h2 id="四、构建发布" tabindex="-1">四、构建发布 <a class="header-anchor" href="#四、构建发布" aria-label="Permalink to &quot;四、构建发布&quot;">​</a></h2><ul><li>Jenkins;</li><li>Webpack;</li><li>Gulp;</li><li>Yarn;</li><li>Docker;</li><li>Rancher;</li><li>Kubernetes;</li><li>Harbor;</li><li>ECS;</li></ul><h2 id="五、学习资料" tabindex="-1">五、学习资料 <a class="header-anchor" href="#五、学习资料" aria-label="Permalink to &quot;五、学习资料&quot;">​</a></h2><p><a href="https://juejin.cn/live/1659039" target="_blank" rel="noreferrer">前端工程化-掘金</a></p><p><a href="https://www.bilibili.com/video/BV1HY4y1r7rJ?p=3" target="_blank" rel="noreferrer">大前端时代的挑战与机遇（深圳场）_哔哩哔哩_bilibili</a></p><p><a href="https://space.bilibili.com/28696526/channel/collectiondetail?sid=44950" target="_blank" rel="noreferrer">程序员山月的个人空间_哔哩哔哩_Bilibili</a></p>',34),o=[r];function n(s,p,d,c,h,u){return e(),i("div",{"data-pagefind-body":!0},o)}const g=l(t,[["render",n]]);export{b as __pageData,g as default};
