import{j as a,b as s,c as r,L as o,aa as t,G as i}from"./chunks/framework.D8w0pJIA.js";const b=JSON.parse('{"title":"DevOps 了解","description":"","frontmatter":{"title":"DevOps 了解","date":"2022-06-12T20:00:00.000Z","tag":["前端工程化"],"categories":["前端进击"]},"headers":[],"relativePath":"posts/growth-record/engineering/tools/dev-ops.md","filePath":"posts/growth-record/engineering/tools/dev-ops.md","lastUpdated":1713496418000}'),l={name:"posts/growth-record/engineering/tools/dev-ops.md"},p=t('<h1 id="devops-了解" tabindex="-1">DevOps 了解 <a class="header-anchor" href="#devops-了解" aria-label="Permalink to &quot;DevOps 了解&quot;">​</a></h1><p>工程化定义：让一堆人做一堆事</p><h2 id="devops-介绍" tabindex="-1">DevOps 介绍 <a class="header-anchor" href="#devops-介绍" aria-label="Permalink to &quot;DevOps 介绍&quot;">​</a></h2><blockquote><p>DevOps（Development和Operations的组合词）是一组过程、方法与系统的统称，用于促进开发（应用程序/软件工程）、技术运营和质量保障（QA）部门之间的沟通、协作与整合。</p><p>它是一种重视“软件开发人员（Dev）”和“IT运维技术人员（Ops）”之间沟通合作的文化、运动或惯例。透过自动化“软件交付”和“架构变更”的流程，来使得构建、测试、发布软件能够更加地快捷、频繁和可靠。</p></blockquote><blockquote><p>它的出现是由于软件行业日益清晰地认识到：为了按时交付软件产品和服务，开发和运维工作必须紧密合作。 ——百度百科</p></blockquote><h2 id="devops-流程" tabindex="-1">DevOps 流程 <a class="header-anchor" href="#devops-流程" aria-label="Permalink to &quot;DevOps 流程&quot;">​</a></h2>',6),n=t('<ol><li>plan：开发计划或需求评审；</li><li>code：开发编码需求功能；</li><li>build：构建打包需求功能；</li><li>test：测试需求；</li><li>release：发布版本；</li><li>deploy：部署上线；</li><li>operate：操作运维；</li><li>monitor：监控。</li></ol><h2 id="devops-内容" tabindex="-1">DevOps 内容 <a class="header-anchor" href="#devops-内容" aria-label="Permalink to &quot;DevOps 内容&quot;">​</a></h2>',2);function d(c,_,h,v,m,g){const e=i("CustomImage");return s(),r("div",null,[p,o(e,{src:"/growth-record/engineering/tools/devops01.webp"}),n,o(e,{src:"/growth-record/engineering/tools/devops02.webp"})])}const D=a(l,[["render",d]]);export{b as __pageData,D as default};
