import{_ as a,D as t,c as o,o as i,a6 as e,I as n}from"./chunks/framework.BOyJDVBP.js";const m=JSON.parse('{"title":"UI 设计与交互体验","description":"","frontmatter":{"title":"UI 设计与交互体验","date":"2022-11-02T20:00:00.000Z","tag":["UI 交互体验"],"categories":["前端进击"]},"headers":[],"relativePath":"posts/growth-record/other/productBusiness/ui-interactive-experience.md","filePath":"posts/growth-record/other/productBusiness/ui-interactive-experience.md","lastUpdated":1713628429000}'),l={name:"posts/growth-record/other/productBusiness/ui-interactive-experience.md"},s=e('<h1 id="ui-设计与交互体验" tabindex="-1">UI 设计与交互体验 <a class="header-anchor" href="#ui-设计与交互体验" aria-label="Permalink to &quot;UI 设计与交互体验&quot;">​</a></h1><p><strong>愿景：希望技术能看见设计的价值，也希望设计能看见技术的力量，在彼此看见中互相融合成长，一起让世界更美好</strong>。</p><p>（1）怎么解决：<strong>研发效率与用户体验（最大化研发效率和极致的用户体验）？</strong></p><p>（2）存在的问题：时常会抱怨自己被当做资源，埋没在无尽的需求中，写着没有灵魂的代码，而反过来看，我们是否有真正使用过自己研发的产品，理解产品需求背后的意义，今天作为研发，是否具有 owner 意识很重要，也要带着自己技术上的沉淀与思考去融入到产品迭代当中。</p><h2 id="组件库设计原则" tabindex="-1">组件库设计原则 <a class="header-anchor" href="#组件库设计原则" aria-label="Permalink to &quot;组件库设计原则&quot;">​</a></h2><ul><li><a href="https://devui.design/design-cn/start" target="_blank" rel="noreferrer">DevUI</a></li><li><a href="https://antv.antgroup.com/specification/principles/basic" target="_blank" rel="noreferrer">AntV</a></li></ul><h2 id="ant-design" tabindex="-1">Ant design <a class="header-anchor" href="#ant-design" aria-label="Permalink to &quot;Ant design&quot;">​</a></h2><p><strong>本文从该组件库设计规范上摸索总结有用的设计知识和交互体验风格。</strong><a href="https://ant-design.antgroup.com/docs/spec/introduce-cn" target="_blank" rel="noreferrer">官网链接</a></p>',8),c=e('<h3 id="一-设计规范" tabindex="-1">（一）设计规范： <a class="header-anchor" href="#一-设计规范" aria-label="Permalink to &quot;（一）设计规范：&quot;">​</a></h3><ul><li>圆角设计为 2px：圆角样式作为 UI 上的重要细节，更小的圆角从视觉上减少界面细节，提升了信息阅读效率。</li><li>无边框组件：在业务中，我们发现有些场景会存在轻量级的选择组件。因而我们提供了一种新的无边框样式，让开发者可以更简单的嵌入这些组件而不用额外覆盖样式。</li></ul><h2 id="设计网站" tabindex="-1">设计网站 <a class="header-anchor" href="#设计网站" aria-label="Permalink to &quot;设计网站&quot;">​</a></h2><ol><li>世界最顶级的设计网站之一，设计灵感来源</li></ol><ul><li><a href="https://dribbble.com/" target="_blank" rel="noreferrer">Dribbble - Discover the World’s Top Designers &amp; Creative Professionals</a></li><li><a href="https://dribbble.com/" target="_blank" rel="noreferrer">Dribbble - Discover the World’s Top Designers &amp; Creative Professionals</a></li></ul><ol start="2"><li>设计插图</li></ol><ul><li><a href="https://www.gaoding.com/" target="_blank" rel="noreferrer">稿定设计-做图做视频必备_在线设计神器_海量版权素材模板</a></li></ul><h2 id="图标库" tabindex="-1">图标库 <a class="header-anchor" href="#图标库" aria-label="Permalink to &quot;图标库&quot;">​</a></h2><p><a href="https://www.iconfont.cn/" target="_blank" rel="noreferrer">iconfont-阿里巴巴矢量图标库</a></p><h2 id="logo-生成器" tabindex="-1">LOGO 生成器 <a class="header-anchor" href="#logo-生成器" aria-label="Permalink to &quot;LOGO 生成器&quot;">​</a></h2><ul><li><a href="https://app.brandmark.io/v2/" target="_blank" rel="noreferrer">Brandmark - make your logo in minutes</a></li><li><a href="https://logo.aliyun.com/logo#/name" target="_blank" rel="noreferrer">阿里云智能logo设计</a></li></ul>',11);function h(d,p,u,_,g,b){const r=t("CustomImage");return i(),o("div",{"data-pagefind-body":!0},[s,n(r,{src:"/growth-record/other/productBusiness/ui-ant-design.webp"}),c])}const k=a(l,[["render",h]]);export{m as __pageData,k as default};
