import{_ as e,c as a,o as r,j as t,a as s}from"./chunks/framework.BOyJDVBP.js";const x=JSON.parse('{"title":"来自陌生人的温暖","description":"","frontmatter":{"title":"来自陌生人的温暖","date":"2020-06-02T20:06:08.000Z","tag":["生活"],"categories":["夜航西飞"]},"headers":[],"relativePath":"posts/random-thought/2020/stranger-warmth.md","filePath":"posts/random-thought/2020/stranger-warmth.md","lastUpdated":1713746846000}'),o={name:"posts/random-thought/2020/stranger-warmth.md"},n=t("h1",{id:"来自陌生人的温暖",tabindex:"-1"},[s("来自陌生人的温暖 "),t("a",{class:"header-anchor",href:"#来自陌生人的温暖","aria-label":'Permalink to "来自陌生人的温暖"'},"​")],-1),d=t("p",null,"今天上午像往常一样赶着去乘地铁，为了赶上八点二十九分的，真的煞费苦心（就是走了有点快），终于赶上了，代价也是不停的猛流汗吧😅，下意识的，已经习惯了，因为不是第一次了，家常便饭吧，这个时候站在门口的阿姨掏出了纸巾便问了我一句：“需要纸巾吗？”，真的瞬间内心凉快好多，没想到一个陌生人的一次温暖的举动就可以感动到我，虽然还是不要脸的要了😃，但是真的很感谢她，可能有些习以为常的事情，对于一个陌生人来说可以做到这样，确实需要一点勇气和爱心的，愿她以后都被人所爱，向阳花开。",-1),c=t("p",null,"有时候也觉得，是不是自己对身边的人奢求太多了，或者一些都觉得该做的东西，还是我对身边的人要求太高了，导致很多事情，并没有那么的重视或者忽略了身边人的感受，是我太自私了吧，我应该好好让他们开心才是，大家都好好的，就好了。",-1),h=[n,d,c];function i(_,p,l,m,g,f){return r(),a("div",{"data-pagefind-body":!0},h)}const $=e(o,[["render",i]]);export{x as __pageData,$ as default};
