import{_ as o,D as l,c as r,o as i,a6 as s,I as e,j as t}from"./chunks/framework.BOyJDVBP.js";const q=JSON.parse('{"title":"第七期训练营茶话会分享","description":"","frontmatter":{"title":"第七期训练营茶话会分享","date":"2022-09-25T20:00:00.000Z","tags":[2022,"训练营"],"categories":["夜航西飞"]},"headers":[],"relativePath":"posts/random-thought/2022/camp-7th-share.md","filePath":"posts/random-thought/2022/camp-7th-share.md","lastUpdated":1713096893000}'),h={name:"posts/random-thought/2022/camp-7th-share.md"},n=s('<h1 id="第七期训练营茶话会分享" tabindex="-1">第七期训练营茶话会分享 <a class="header-anchor" href="#第七期训练营茶话会分享" aria-label="Permalink to &quot;第七期训练营茶话会分享&quot;">​</a></h1><p>2022 年 9 月 25 号，首次参加 21 天训练营茶话会分享（可惜😭当时没有录屏，缺少音频分享出来了），分享内容如下：</p><h2 id="自我介绍" tabindex="-1">自我介绍 <a class="header-anchor" href="#自我介绍" aria-label="Permalink to &quot;自我介绍&quot;">​</a></h2><ul><li>19 年科班毕业</li><li>深圳某中厂</li><li>爱折腾技术，技术栈以：Vue/多端开发/Node 为主</li><li>准备学摄影，喜欢看风景（每年一次旅游）</li><li>周末喜欢看书（计划每个月看一本书）</li></ul><h2 id="关于训练营" tabindex="-1">关于训练营 <a class="header-anchor" href="#关于训练营" aria-label="Permalink to &quot;关于训练营&quot;">​</a></h2><p>参加了两期：8月份和 9 月份</p><h4 id="收获" tabindex="-1">收获 <a class="header-anchor" href="#收获" aria-label="Permalink to &quot;收获&quot;">​</a></h4><ol><li>良好的学习氛围：</li></ol><ul><li>有组织</li><li>有互动</li><li>不止为了打卡</li></ul><ol start="2"><li>阅读和写作：</li></ol><ul><li><p>第七期：<a href="https://www.zhihu.com/question/22355368/answer/2536811740?utm_campaign=shareopn&amp;utm_content=group2_Answer&amp;utm_medium=social&amp;utm_oi=46320467312640&amp;utm_psn=1539039746155761664&amp;utm_source=wechat_session&amp;s_r=0" target="_blank" rel="noreferrer">每天练习写作，应该写什么来练习</a>说到：“每天练习写作的真正目的，是为了摆脱对灵感的过渡依赖，让灵感成为写作的锦上添花，而不是雪中送炭”</p></li><li><p>第八期：《认知驱动》第四章第二节：<a href="https://www.yuque.com/super456/qoriag/frmn7b#Z7yTN" target="_blank" rel="noreferrer">写下来：我们都低估了“写下来”的力量</a>阅读笔记</p></li></ul><ol start="3"><li>有更多的时间做自己想做的事情，不止学习</li></ol><h4 id="建议" tabindex="-1">建议 <a class="header-anchor" href="#建议" aria-label="Permalink to &quot;建议&quot;">​</a></h4><ul><li>希望大家融入进来，当然不只为了打卡</li><li>可以不定期组织线上或线下活动，可以有某个主题分享或讨论会（比如谈谈写作、成长、技术、学习方法、生活等）</li><li>价值输出，成为一个有价值的人，成就更美好的自己</li></ul><h2 id="关于自身成长" tabindex="-1">关于自身成长 <a class="header-anchor" href="#关于自身成长" aria-label="Permalink to &quot;关于自身成长&quot;">​</a></h2><p>分享自身的职业成长路线</p><ol><li>建立自己的技术体系：让时间为你证明</li></ol>',17),u=t("ol",{start:"2"},[t("li",null,"分享羽哥之前说的一句话：")],-1);function c(m,d,p,_,f,b){const a=l("CustomImage");return i(),r("div",{"data-pagefind-body":!0},[n,e(a,{src:"/random-thought/2022/jishutixi.webp"}),u,e(a,{src:"/random-thought/2022/yugeduihua.webp"})])}const w=o(h,[["render",c]]);export{q as __pageData,w as default};
