import{j as e,b as t,c as o,L as s,aa as r,s as n,G as l}from"./chunks/framework.D8w0pJIA.js";const T=JSON.parse('{"title":"转岗选择","description":"","frontmatter":{"title":"转岗选择","date":"2022-12-02T20:09:08.000Z","tag":["工作"],"categories":["夜航西飞"]},"headers":[],"relativePath":"posts/random-thought/2022/job-transfer-selection.md","filePath":"posts/random-thought/2022/job-transfer-selection.md","lastUpdated":1713746846000}'),i={name:"posts/random-thought/2022/job-transfer-selection.md"},c=r('<h1 id="转岗选择" tabindex="-1">转岗选择 <a class="header-anchor" href="#转岗选择" aria-label="Permalink to &quot;转岗选择&quot;">​</a></h1><h2 id="面临选择" tabindex="-1">面临选择 <a class="header-anchor" href="#面临选择" aria-label="Permalink to &quot;面临选择&quot;">​</a></h2><p>我面临选择的是否转岗问题：周五大领导找我谈话，希望我去另一个部门前端组当负责人，那个组前端有 3 个人，我是去还是不去？</p><p>影响点：</p><ul><li>那个部门是定制化甲方项目，长期战略合作的，有点类似外包项目，工作很多回比较受限。相对我现在部门是工作比较忙；</li><li>前端组负责人相对我现在部门只是一个业务开发前端，去了之后对个人成长会提升很多，相对的技术学习和专注肯定会有损失，工作 &amp; 生活时间需要重新平衡；</li><li>需要面对的挑战更多和未知的因素，需要学会管理和团队协作，不止是关注自己的手头上的事了；</li></ul><p>我犹豫的几个点：</p><ul><li>现在部门工作轻松，有更多的时间做自己想做的事情（花了很多业余时间在学习成长上，收获非常多）。如果一旦过去了之后学习时间相对很少且肯定会牺牲很多自己的时间；</li><li>现在工作快三年半了，我是想工作 5 年内更专注自己的技术深度上，毕竟想着靠技术这条路可以走的更长远一点，如果太早转管理岗，会有点影响的；</li><li>我一直有个大厂梦，也就是希望在自己 5 年内可以进入梦想的大厂，所以需要付出很多时间和精力在个人技术成长上，所以懂得都懂，哈哈哈；</li></ul><h2 id="结果" tabindex="-1">结果 <a class="header-anchor" href="#结果" aria-label="Permalink to &quot;结果&quot;">​</a></h2>',8),d=n("p",null,"领导说的挺好的，我也认可。但有点失望，并不是不能转岗，而是对方这个口语表达，他好像并不理解我说的刷题意思，哈哈哈，可能语言真的是门艺术吧",-1);function h(_,p,m,u,f,g){const a=l("CustomImage");return t(),o("div",null,[c,s(a,{src:"/random-thought/2022/zhuangang.png"}),d])}const P=e(i,[["render",h]]);export{T as __pageData,P as default};
