import{_ as a,c as e,o as t,a6 as n}from"./chunks/framework.BOyJDVBP.js";const m=JSON.parse('{"title":"第 7 本《蛤蟆先生去看心理医生》","description":"蛤蟆先生一向爱笑爱闹，如今却一反常态地郁郁寡欢，他一个人躲在屋里，连起床梳洗的力气都没有。朋友们非常担心他，建议他去做心理咨询。在 10 次心理咨询中，蛤蟆在咨询师苍鹭的带领下，勇敢地探索了自己的内心世界，也逐渐找回了信心与希望…… 这并不是一本写给孩子看的书，而是一本非常有深度的心理疗愈读物。出版 20 多年来，已成为英国国民级心理咨询入门书。它基于 TA 沟通分析心理学的理论，讲述了一个抑郁症病人通过十次心理咨询，终于找回快乐和自信的全过程。在书中，可以看到心理咨询的倾听、共情、沟通技巧，也可以看到一个标准的心理咨询流程的模板。读者犹如亲临现场，体验心理咨询的每一个细节，见证疗愈和改变的发生 在这本书里，作者借由蛤蟆和心理咨询师苍鹭的互动，探索了蛤蟆自卑、软弱、爱炫耀的个性与抑郁的情绪究竟来源于何处，让读者看到童年经历对人格的深刻影响，以及如何才能在心理上真正长大成人，独立、自信、充满希望地生活","frontmatter":{"title":"第 7 本《蛤蟆先生去看心理医生》","description":"蛤蟆先生一向爱笑爱闹，如今却一反常态地郁郁寡欢，他一个人躲在屋里，连起床梳洗的力气都没有。朋友们非常担心他，建议他去做心理咨询。在 10 次心理咨询中，蛤蟆在咨询师苍鹭的带领下，勇敢地探索了自己的内心世界，也逐渐找回了信心与希望…… 这并不是一本写给孩子看的书，而是一本非常有深度的心理疗愈读物。出版 20 多年来，已成为英国国民级心理咨询入门书。它基于 TA 沟通分析心理学的理论，讲述了一个抑郁症病人通过十次心理咨询，终于找回快乐和自信的全过程。在书中，可以看到心理咨询的倾听、共情、沟通技巧，也可以看到一个标准的心理咨询流程的模板。读者犹如亲临现场，体验心理咨询的每一个细节，见证疗愈和改变的发生 在这本书里，作者借由蛤蟆和心理咨询师苍鹭的互动，探索了蛤蟆自卑、软弱、爱炫耀的个性与抑郁的情绪究竟来源于何处，让读者看到童年经历对人格的深刻影响，以及如何才能在心理上真正长大成人，独立、自信、充满希望地生活","date":"2022-07-25T20:00:00.000Z","cover":"/read-book/2022/hamaxianshengkanxingliyisheng.webp","tags":[2022,"心理"],"categories":["朝花夕拾"]},"headers":[],"relativePath":"posts/read-book/2022/hamaxianshengkanxingliyisheng.md","filePath":"posts/read-book/2022/hamaxianshengkanxingliyisheng.md","lastUpdated":1713076178000}'),i={name:"posts/read-book/2022/hamaxianshengkanxingliyisheng.md"},o=n("",7),s=[o];function r(h,d,c,_,l,p){return t(),e("div",{"data-pagefind-body":!0},s)}const x=a(i,[["render",r]]);export{m as __pageData,x as default};
