import{j as e,b as a,c as r,aa as t}from"./chunks/framework.BdPFiiJb.js";const f=JSON.parse('{"title":"第 9 本《认知驱动》","description":"成长的根本是：创造价值、做成事情。从“习惯养成”到“技能培养”，从“对内输入”到“对外输出”，从成为“更好的人”到成为“更有价值的人”，这些刻意的转变会让我们走出焦虑的怪圈 做到，是最高等级的成长。一个人最终的成长也是通过主动做成一件对他人很有用的事来实现的。但现实生活中，很多人虽然内心强烈希望变好，行动也非常努力，却始终无法做成事情，这背后的原因通常是因为缺少价值意识、输出意识、创造意识、作品意识、利他意识…… 本书将通过“做成一件事”的心法和技法两部分内容，和大家一起探索如何通过创造个人价值来获取人生的成就、幸福和意义 单纯依靠意志力的努力往往是盲目的，所以人们会反反复复地起念，又反反复复的失败。只有当一个人彻底了解了做成一件事的来龙去脉、看清事情本质后，才能彻底走出反复尝试却劳而无功的困境 学会用认知来驱动自己，我们就能更好地到达成长的彼岸——做成一件对他人很有用的事，成为一个有价值的人","frontmatter":{"title":"第 9 本《认知驱动》","description":"成长的根本是：创造价值、做成事情。从“习惯养成”到“技能培养”，从“对内输入”到“对外输出”，从成为“更好的人”到成为“更有价值的人”，这些刻意的转变会让我们走出焦虑的怪圈 做到，是最高等级的成长。一个人最终的成长也是通过主动做成一件对他人很有用的事来实现的。但现实生活中，很多人虽然内心强烈希望变好，行动也非常努力，却始终无法做成事情，这背后的原因通常是因为缺少价值意识、输出意识、创造意识、作品意识、利他意识…… 本书将通过“做成一件事”的心法和技法两部分内容，和大家一起探索如何通过创造个人价值来获取人生的成就、幸福和意义 单纯依靠意志力的努力往往是盲目的，所以人们会反反复复地起念，又反反复复的失败。只有当一个人彻底了解了做成一件事的来龙去脉、看清事情本质后，才能彻底走出反复尝试却劳而无功的困境 学会用认知来驱动自己，我们就能更好地到达成长的彼岸——做成一件对他人很有用的事，成为一个有价值的人","date":"2022-10-01T20:00:00.000Z","cover":"/read-book/2022/renzhiqudong.webp","tags":[2022,"认知"],"categories":["朝花夕拾"]},"headers":[],"relativePath":"posts/read-book/2022/renzhiqudong.md","filePath":"posts/read-book/2022/renzhiqudong.md","lastUpdated":1713076178000}'),o={name:"posts/read-book/2022/renzhiqudong.md"},n=t("",11),i=[n];function s(l,p,d,h,c,u){return a(),r("div",null,i)}const q=e(o,[["render",s]]);export{f as __pageData,q as default};
