import{_ as o,c as e,o as t,a6 as u}from"./chunks/framework.BOyJDVBP.js";const n=JSON.parse('{"title":"第 28 本《循序渐进 Vue.js 3 前端开发》","description":"本书以一个多年前端“老司机”的视角，循序渐进地介绍当前流行的前端框架Vue.js 3的新特性、各项功能及其在商业开发中的应用。全书共15章，第1~6章介绍Vue.js 3的模板、组件、交互处理等基础知识；第7章介绍Vue.js 3框架的响应式原理及组合式API；第8章介绍使用Vue.js 3框架开发前端动画效果；第9章介绍开发大型项目必备的脚手架工具VueCli和Vite；第10章介绍基于Vue.js 3的UI框架Element Plus；第11~13章分别介绍网络请求框架vue-axios、路由管理框架Vue Router、状态管理框架Vuex；第14章和第15章介绍两个相对完整的项目的开发，即学习网站和电商后台系统。本书试图介绍Vue.js 3全家桶及周边框架和工具的综合应用，旨在使读者通过阅读本书开发自己的应用程序。本书还在各章安排了小型范例和练习题，并提供了教学视频、源代码及PPT课件。\\n本书既可以入门，也可以进阶，适合Vue.js 3初学者和前端开发人员使用，也可以作为网课、培训机构与大中专院校的教学用书。","frontmatter":{"title":"第 28 本《循序渐进 Vue.js 3 前端开发》","description":"本书以一个多年前端“老司机”的视角，循序渐进地介绍当前流行的前端框架Vue.js 3的新特性、各项功能及其在商业开发中的应用。全书共15章，第1~6章介绍Vue.js 3的模板、组件、交互处理等基础知识；第7章介绍Vue.js 3框架的响应式原理及组合式API；第8章介绍使用Vue.js 3框架开发前端动画效果；第9章介绍开发大型项目必备的脚手架工具VueCli和Vite；第10章介绍基于Vue.js 3的UI框架Element Plus；第11~13章分别介绍网络请求框架vue-axios、路由管理框架Vue Router、状态管理框架Vuex；第14章和第15章介绍两个相对完整的项目的开发，即学习网站和电商后台系统。本书试图介绍Vue.js 3全家桶及周边框架和工具的综合应用，旨在使读者通过阅读本书开发自己的应用程序。本书还在各章安排了小型范例和练习题，并提供了教学视频、源代码及PPT课件。\\n本书既可以入门，也可以进阶，适合Vue.js 3初学者和前端开发人员使用，也可以作为网课、培训机构与大中专院校的教学用书。","date":"2023-12-06T20:00:00.000Z","cover":"/read-book/2023/xunxujianjinVue3.webp","tags":[2023,"专业技术"],"categories":["朝花夕拾"]},"headers":[],"relativePath":"posts/read-book/2023/xunxujianjinVue3.md","filePath":"posts/read-book/2023/xunxujianjinVue3.md","lastUpdated":1713076178000}'),c={name:"posts/read-book/2023/xunxujianjinVue3.md"},l=u("",67),b=[l];function k(q,p,a,r,s,i){return t(),e("div",{"data-pagefind-body":!0},b)}const d=o(c,[["render",k]]);export{n as __pageData,d as default};
