import{_ as o,c as e,o as t,a6 as a}from"./chunks/framework.BOyJDVBP.js";const h=JSON.parse('{"title":"第 41 本《没事，一起都会好起来的》","description":"韩国人气插画师 Jedit 在网络上发起了 365 白日梦计划，在 356 天内，每天发表一幅梦幻、如白日梦场景的插画，本书收录了其中最受好评的 115 幅插画。在 Jedit 的画作中，经常出现如上弦月、飞天鲸鱼，像精灵般的小人等神奇元素，也有街头午后、美丽花园、公园等寻常场景，融合了现实与非现实的不可思议感。 Jedit 并不总是只描绘幻想中的世界。我们日常生活中的碎片也在其笔下像神秘的场面一样诞生。在向路边的猫打招呼的瞬间，进入胡同拐角的瞬间，在河的桥上散步的瞬间等谁都经历过的瞬间，在其笔下就像拥有魔法一般，使得我们的日常生活也通过绘作变得特别。 他还为每幅画作配上几句短短的花絮，这些彷佛摘录于长篇故事的话语片段，让每一幅画作都充满了故事感！适合在睡前、想要安慰或感到沮丧时，或者心里感到疲惫、不安的时候阅读","frontmatter":{"title":"第 41 本《没事，一起都会好起来的》","description":"韩国人气插画师 Jedit 在网络上发起了 365 白日梦计划，在 356 天内，每天发表一幅梦幻、如白日梦场景的插画，本书收录了其中最受好评的 115 幅插画。在 Jedit 的画作中，经常出现如上弦月、飞天鲸鱼，像精灵般的小人等神奇元素，也有街头午后、美丽花园、公园等寻常场景，融合了现实与非现实的不可思议感。 Jedit 并不总是只描绘幻想中的世界。我们日常生活中的碎片也在其笔下像神秘的场面一样诞生。在向路边的猫打招呼的瞬间，进入胡同拐角的瞬间，在河的桥上散步的瞬间等谁都经历过的瞬间，在其笔下就像拥有魔法一般，使得我们的日常生活也通过绘作变得特别。 他还为每幅画作配上几句短短的花絮，这些彷佛摘录于长篇故事的话语片段，让每一幅画作都充满了故事感！适合在睡前、想要安慰或感到沮丧时，或者心里感到疲惫、不安的时候阅读","date":"2024-02-04T20:00:00.000Z","cover":"/read-book/2024/meishiyiqidouhuihaoqilaide.webp","tags":[2024,"漫画"],"categories":["朝花夕拾"]},"headers":[],"relativePath":"posts/read-book/2024/meishiyiqidouhuihaoqilaide.md","filePath":"posts/read-book/2024/meishiyiqidouhuihaoqilaide.md","lastUpdated":1713076178000}'),i={name:"posts/read-book/2024/meishiyiqidouhuihaoqilaide.md"},c=a("",21),l=[c];function p(d,u,q,r,s,b){return t(),e("div",{"data-pagefind-body":!0},l)}const n=o(i,[["render",p]]);export{h as __pageData,n as default};
