import{j as e,b as t,c as a,aa as r}from"./chunks/framework.D8w0pJIA.js";const f=JSON.parse('{"title":"第 51 本《大前端三剑客——Vue+React+Flutter》","description":"","frontmatter":{"title":"第 51 本《大前端三剑客——Vue+React+Flutter》","cover":"/read-book/2024/three-frame.jpeg","tags":[2024,"专业技术"],"categories":["朝花夕拾"]},"headers":[],"relativePath":"posts/read-book/2024/three-frame.md","filePath":"posts/read-book/2024/three-frame.md","lastUpdated":1715184572000}'),o={name:"posts/read-book/2024/three-frame.md"},s=r('<h1 id="第-51-本《大前端三剑客——vue-react-flutter》" tabindex="-1">第 51 本《大前端三剑客——Vue+React+Flutter》 <a class="header-anchor" href="#第-51-本《大前端三剑客——vue-react-flutter》" aria-label="Permalink to &quot;第 51 本《大前端三剑客——Vue+React+Flutter》&quot;">​</a></h1><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>随着移动互联时代到万物互联超级终端时代的变化，开发者也从移动互联开发转型到万物互联时代的超级终端开发，本书全面讲解大前端时代的核心技术栈和核心开发语言，并通过一本书融汇贯通，本书是普通前端开发者通向大前端架构师的桥梁，本书中涉及大量案例和企业一线实践操作经验，是前端开发者转型为大前端架构师的必备书籍</p><p>本书共4篇15个章。第一篇为基础篇（第1~6章），主要介绍大前端的发展趋势，大前端的基础开发语言(ES6、TypeScript、Dart)，大前端构建工具和前端工程化体系，以及大前端的包管理和如何搭建一个企业级的脚手架工具。从第二篇至第四篇（第7~15章），分别介绍Vue 3、React和Flutter 2三大主流框架，帮助开发者学习和掌握**的框架用法和生态体系。 学习本书内容，需要具备一定的HTML、CSS、JS基础知识，本书可以作为前端开发者提升技能的工具书也可以作为普通开发者从网页开发过渡到万物互联开发的书籍</p><h2 id="阅读笔记" tabindex="-1">阅读笔记 <a class="header-anchor" href="#阅读笔记" aria-label="Permalink to &quot;阅读笔记&quot;">​</a></h2><p>待补充读后感🚀...</p>',6),c=[s];function l(d,i,n,h,_,u){return t(),a("div",null,c)}const m=e(o,[["render",l]]);export{f as __pageData,m as default};
