import{_ as o,c as e,o as t,a6 as l}from"./chunks/framework.BOyJDVBP.js";const n=JSON.parse('{"title":"第 35 本《我在北京送快递》","description":"进入社会工作至今的二十年间，胡安焉走南闯北，辗转于广东、广西、云南、上海、北京等地，做过快递员、夜班拣货工人、便利店店员、保安、自行车店销售、服装店导购、加油站加油工……他将日常的点滴和工作的甘苦化作真诚的自述，记录了一个平凡人在生活中的辛劳、私心、温情、正气。 在物流公司夜间拣货的一年，给他留下了深刻的生理印记：“这份工作还会令人脾气变坏，因为长期熬夜以及过度劳累，人的情绪控制力会明显下降……我已经感到脑子不好使了，主要是反应变得迟钝，记忆力开始衰退。”在北京送快递的两年，他“把自己看作一个时薪30元的送货机器，达不到额定产出值就恼羞成怒、气急败坏”…… 但他最终认识到，怀着怨恨的人生是不值得过的。这些在事后追忆中写成的工作经历，渗透着他看待生活和世界的态度与反思，旨在表达个人在有限的选择和局促的现实中，对生活意义的直面和肯定：生活中许多平凡隽永的时刻，要比现实困扰的方方面面对人生更具有决定意义","frontmatter":{"title":"第 35 本《我在北京送快递》","description":"进入社会工作至今的二十年间，胡安焉走南闯北，辗转于广东、广西、云南、上海、北京等地，做过快递员、夜班拣货工人、便利店店员、保安、自行车店销售、服装店导购、加油站加油工……他将日常的点滴和工作的甘苦化作真诚的自述，记录了一个平凡人在生活中的辛劳、私心、温情、正气。 在物流公司夜间拣货的一年，给他留下了深刻的生理印记：“这份工作还会令人脾气变坏，因为长期熬夜以及过度劳累，人的情绪控制力会明显下降……我已经感到脑子不好使了，主要是反应变得迟钝，记忆力开始衰退。”在北京送快递的两年，他“把自己看作一个时薪30元的送货机器，达不到额定产出值就恼羞成怒、气急败坏”…… 但他最终认识到，怀着怨恨的人生是不值得过的。这些在事后追忆中写成的工作经历，渗透着他看待生活和世界的态度与反思，旨在表达个人在有限的选择和局促的现实中，对生活意义的直面和肯定：生活中许多平凡隽永的时刻，要比现实困扰的方方面面对人生更具有决定意义","date":"2024-01-13T20:00:00.000Z","cover":"/read-book/2024/wozaibeijingsongkuaidi.webp","tags":[2024,"人生"],"categories":["朝花夕拾"]},"headers":[],"relativePath":"posts/read-book/2024/wozaibeijingsongkuaidi.md","filePath":"posts/read-book/2024/wozaibeijingsongkuaidi.md","lastUpdated":1713076178000}'),c={name:"posts/read-book/2024/wozaibeijingsongkuaidi.md"},b=l("",93),u=[b];function k(q,p,a,i,r,s){return t(),e("div",{"data-pagefind-body":!0},u)}const _=o(c,[["render",k]]);export{n as __pageData,_ as default};
